export default {
	delay: async (time)=>{
		return new Promise((resolve)=>{
			setTimeout(()=>{resolve()},time)
		})
	},
	createLeaveRequestSheetForMonth: async () => {
		//use async-await or promises
		await getLeaveRequestSpreadsheet.run()
		const spreadsheetData = getLeaveRequestSpreadsheet.data
		const currentTitle = moment().format('YYYY')
		if(!spreadsheetData.sheets.find((sheet)=>sheet.title===currentTitle)){
			await addLeaveRequestsSheet.run({title:currentTitle})
			await initTitleLeaveRequestSheet.run({sheet:currentTitle})
		}

		const nextTitle = moment().add(1,'years').format('YYYY')
		if(!spreadsheetData.sheets.find((sheet)=>sheet.title===nextTitle)){
			await addLeaveRequestsSheet.run({title:nextTitle})
			await initTitleLeaveRequestSheet.run({sheet:nextTitle})
		}
	}
}
