export default {
	myVar1: [],
	storage: {
		startTime:moment().toLocaleString()
	},
	formatStartTime:(startTimeString)=>{
		const startTimeMoment = moment(startTimeString)

		let hourToSet=8;
		if(startTimeMoment.hours()>=12) hourToSet=12
		startTimeMoment.set({hour:hourToSet, minute:0,second:0,milisecond:0})

		return startTimeMoment.toLocaleString()
	},
	formatEndTime:(endTimeString)=>{
		const timeMoment = moment(endTimeString)
		let hourToSet=18;
		if(timeMoment.hours()<=13) hourToSet=12
		timeMoment.set({hour:hourToSet, minute:0,second:0,milisecond:0})

		return timeMoment.toLocaleString()
	},
	calculateEndTime: (startTimeString,totalDay) => {
		const startTimeMoment=moment(this.formatStartTime(startTimeString))
		const hasHalfDay = (totalDay-Math.floor(totalDay))>0
		if(Math.floor(totalDay)>=1){
			startTimeMoment.add( (Math.floor(totalDay)-1) * 24 + 8,'hour')
		}
		if(hasHalfDay){
			startTimeMoment.add(5,'hour')
		}
		return this.formatEndTime(startTimeMoment.toLocaleString())
	},
	isValidateLeaveRequestTime:(startTimeString,endTimeString,listLeaveRequestRequestOrAccept)=>{
		const startTimeMoment = moment(startTimeString)
		const endTimeMoment = moment(endTimeString)
		if(startTimeMoment.isBefore(moment().set({hour:0})) || endTimeMoment.isBefore(startTimeMoment)){
			return false
		}
		const isNotValid = listLeaveRequestRequestOrAccept.some((leaveRequestOrAccept)=>{
			const startMoment = moment(leaveRequestOrAccept['Start Date'])
			const endMoment = moment(leaveRequestOrAccept['End Date'])
			return (startTimeMoment.diff(startMoment)>=0 && startTimeMoment.diff(endMoment) <=0) || (endTimeMoment.diff(startMoment)>=0 && endTimeMoment.diff(endMoment) <=0)			
		})
		return !isNotValid
	},
	getConfirmText(startTime,endTime,listLeaveRequestRequestOrAccept){
