async () => {
  const spreadsheetData = getLeaveRequestSpreadsheet.data;
  const currentMonthTitle = moment().format('YYYY-MMM');
  if (!spreadsheetData.sheets.find(sheet => sheet.title === currentMonthTitle)) {
    await addLeaveRequestsSheet.run({
      title: currentMonthTitle
    });
    await initTitleLeaveRequestSheet.run({
      sheet: currentMonthTitle
    });
  }
  const nextMonthTitle = moment().add(1, 'months').format('YYYY-MMM');
  if (!spreadsheetData.sheets.find(sheet => sheet.title === nextMonthTitle)) {
    await addLeaveRequestsSheet.run({
      title: nextMonthTitle
    });
    await initTitleLeaveRequestSheet.run({
      sheet: nextMonthTitle
    });
  }
}

