(startTimeString, totalDay) => {
  const startTimeMoment = moment(Utils.formatStartTime(startTimeString));
  const hasHalfDay = totalDay - Math.floor(totalDay) > 0;
  if (Math.floor(totalDay) >= 1) {
    startTimeMoment.add((Math.floor(totalDay) - 1) * 24 + 8, 'hour');
  }
  if (hasHalfDay) {
    startTimeMoment.add(5, 'hour');
  }
  return Utils.formatEndTime(startTimeMoment.toLocaleString());
}

