actionConfiguration.body:
(startTimeString, totalDay) => {
  const startTimeMoment = moment(Utils.formatStartTime(startTimeString));
  if (startTimeMoment.hours() !== 12) {
    startTimeMoment.set({
      hour: 0
    });
  }
  let halfDayCounter = totalDay / 0.5;
  while (halfDayCounter > 0) {
    if (startTimeMoment.day() === 0 || startTimeMoment.day() === 6 && startTimeMoment.hours() === 12) {
      startTimeMoment.add({
        hour: 12
      });
      continue;
    }
    startTimeMoment.add({
      hour: 12
    });
    halfDayCounter -= 1;
  }
  startTimeMoment.subtract(1, 'hour');
  return Utils.formatEndTime(startTimeMoment.toLocaleString());
}

