export default {
	approveRequest: async (leaveRequestData) => {
		// update Avaiable Leaves and Leaves Applied
		const userInfo = listStaff.data.find(c=>c.Email===leaveRequestData.Email)
		if(!userInfo) showAlert(`User ${userInfo.Email} is not existed`)
		await updateLeaveCount.run({
			rowIndex:userInfo.rowIndex,
			leaveDays:leaveRequestData["Total Days"]
		})

		await approveLeaveRequest.run({
			rowIndex: leaveRequestData.rowIndex
		})
		listStaff.run()
		listLeaveRequested.run()
	},
	rejectRequest: async (leaveRequestData) => {
		await rejectLeaveRequest.run({
			rowIndex: leaveRequestData.rowIndex,		
			reason:""
		})
		listLeaveRequested.run()
	}
}
