async () => {
  await getTimeKeepingSpreadsheet.run();
  const spreadsheetData = getTimeKeepingSpreadsheet.data;
  const currentTitle = moment().format('YYYY-MM');
  if (!spreadsheetData.sheets.find(sheet => sheet.title === currentTitle)) {
    await addTimeKeepingSheet.run({
      title: currentTitle
    });
    await initTitleTimeKeepingSheet.run({
      sheet: currentTitle
    });
  }
  const nextTitle = moment().add(1, 'months').format('YYYY-MM');
  if (!spreadsheetData.sheets.find(sheet => sheet.title === nextTitle)) {
    await addTimeKeepingSheet.run({
      title: nextTitle
    });
    await initTitleTimeKeepingSheet.run({
      sheet: nextTitle
    });
  }
}

